import CustomButton from '../CustomButton/CustomButton.component';
import Slider from '../Slider/Slider.component';
import {connect} from 'react-redux';
import {addItem} from '../../src/redux/cart/cart.actions'
import {motion} from 'framer-motion'
import React,{useState} from 'react'

const easing = [.6, -.05, .01, .99]

const fadeInUp = {
    initial: {
        y:60,
        opacity: 0
    },
    animate: {
        y:0,
        opacity:1,
        transition: {
            duration: .3,
            ease: easing
        }
    }
};


const Card = ({item, addItem, variants}) => {
    const {id,name,price,gallery} =  item

    const [rotate,setRotate] = useState(0)

    const toggleRotate = () => setRotate(rotate+90)

    return (
        <motion.div className='card'
        variants={fadeInUp}
        >            
            <motion.div
                animate={{rotate}}
            >
                <Slider images={gallery} id={id}/>
            </motion.div>            
                <div className='card-content'>
                    <div>               
                        {name}
                        
                    </div>
                    <div> {price} </div>
                </div>
            <div>
                <CustomButton onClick={()=> {toggleRotate();addItem(item)}}>
                   <img className='icon' alt='cart-icon' src ='/icon.svg'/> <div>Add to Cart</div>
                </CustomButton>
            </div>
        </motion.div>
        
    )
}

const mapDispatchToProps = dispatch => ({
    addItem: item => dispatch(addItem(item))
})




export default connect(null, mapDispatchToProps)(Card);
