import React, { Fragment } from 'react';
import Header from '../Header/header.component';
import Head from 'next/head'
import {connect} from 'react-redux';
import {auth, createUserProfileDocument} from '../../firebase/firebase.utils';
import { setCurrentUser } from '../../src/redux/user/user.action';
import { Fire, getMessages } from '../../firebase/firebase.utils';
import {setChannel,setMessages} from '../../src/redux/chat/chat.actions'


class Layout extends React.Component  {

    unsubscribeFromAuth = null;

    componentDidMount() {
        const {setCurrentUser, setMessages, channel} = this.props;
        
        this.unsubscribeFromAuth = auth.onAuthStateChanged(async userAuth =>{
            if (userAuth) {
                const userRef = await createUserProfileDocument(userAuth);
                userRef.onSnapshot(snapShot => {
                    setCurrentUser({
                        id: snapShot.id,
                        ...snapShot.data()
                    })
                })
            };

        })
        if (!channel.length) {
            setChannel(new Date)
        }
        setMessages(getMessages(channel))
    


    }


    componentWillUnmount() {
        this.unsubscribeFromAuth();
        getMessages();
    }


    render() {
        return (
            <Fragment >
                <Head>
                    <link href={`https://fonts.googleapis.com/css2?family=Lora:ital@0;1&family=Open+Sans:wght@400;600;700&display=swap`} rel="stylesheet"/>
                    <link href={`https://fonts.googleapis.com/css2?family=Playfair+Display:wght@400;600;700;800&display=swap`} rel="stylesheet"/>
                    
                    <meta name="viewport" content="width=device-width, initial-scale=1, shrink-to-fit=no"/>
                    <meta name="theme-color" content="#425566"/>
                    <meta name="Description" content="описание сайта"/>
                    <meta httpEquiv="Content-Type" content="text/html; charset=utf-8" charSet="utf-8"/>
                </Head>
                <Header />
                <div className='main'>{this.props.children}</div>

            </Fragment>
        )
    }
}


const mapStateToProps = (state) => ({
    currentUser: state.user.currentUser,
    channel: state.chat.channel
})

const mapDispatchToProps = dispatch => ({
    setCurrentUser: user => dispatch(setCurrentUser(user)),
    setMessages: messages => dispatch(setMessages(messages)),
    setChannel: channel => dispatch(setChannel(channel))
})


export default connect(mapStateToProps,mapDispatchToProps)(Layout);
