import React from 'react';
import Gallery from '../components/Gallery/gallery.component';
import API_URL from '../routes.js';

class ItemGallery extends React.Component {
    constructor(props) {
        super(props)
    }
    
    // fetch(`${API_URL}/bouquets`)
    // .then(res => res.json())
    // .then(data => this.setState({items:[...this.state.items,...data]}))
    // fetch(`${API_URL}/accessories`)
    // .then(res => res.json())
    // .then(data => this.setState({items:[...this.state.items,...data]}))
    // fetch(`${API_URL}/categories`)
    // .then(res => res.json())
    // .then(data => this.setState({categories:data}))

    getInitialProps = async () => {
        const res = await fetch(`${API_URL}/bouquets`)
        const data = await res.json()
        await console.log(data)
        return {items:data}
    }
    
    render() {
        return (
            <div>
                {console.log(this.items)}
                <Gallery/>
            </div>
        )
    }
}

export default ItemGallery;
