import React, {useState, useEffect} from 'react'
import AreaInput from '../AreaInput/area-input.component';
import {sendMessage} from '../../firebase/firebase.utils';
import {connect} from 'react-redux'; 

const Chat =({messages, channel}) => {

    const [text, setText] = useState('')

    useEffect(() => {


    }) 
    const handleSubmit = async event => {
        event.preventDefault();
        const userRef = await sendMessage(text, channel);
        console.log(userRef)
    }


    const handleChange = event => {


            setText(event.target.value)

    }

    return (
        <form onSubmit={handleSubmit}>
            <div>
                {   messages ?
                    messages.map(message=> <p>{message.text}</p>)
                    :null
                }
            </div>

            <AreaInput 
            name='text'
            handleChange={handleChange}
            value={text}
            label='Your Review'
            required
            />       
            <div>
                <button type='submit'>
                    Send
                </button>
            </div>
        </form>
    )
}

const mapStateToProps = state => ({
    messages: state.chat.messages,
    channel: state.chat.channel
})

export default connect(mapStateToProps)(Chat);
