import React from 'react'
import AreaInput from '../AreaInput/area-input.component';
import {sendMessage, getMessages} from '../../firebase/firebase.utils';
import {connect} from 'react-redux'; 
import {setMessages} from '../../src/redux/chat/chat.actions';

class  Chat extends React.Component {
    constructor() {
        super()
        this.state = {
            text:''
        }
    }

    componentDidMount() {
        this.props.setMessages(getMessages(this.props.channel))
    }
    

    handleSubmit = async event => {
        event.preventDefault();
        const userRef = await sendMessage(text, this.props.channel);
        console.log(userRef)
    }


    handleChange = event => {


            this.setState({text:event.target.value})

    }
    render () {
        const {messages,channel} = this.props
        return (
            <form onSubmit={this.handleSubmit}>
                <div>
                    {   messages ?
                        messages.map(message=> <p>{message.text}</p>)
                        :null
                    }
                </div>

                <AreaInput 
                name='text'
                handleChange={this.handleChange}
                value={this.state.text}
                label='Your Review'
                required
                />       
                <div>
                    <button type='submit'>
                        Send
                    </button>
                </div>
            </form>
    )}
}

const mapStateToProps = state => ({
    messages: state.chat.messages,
    channel: state.chat.channel
})

const mapDispatchToProps = dispatch => ({
    setMessages: messages => dispatch(setMessages(messages))
})

export default connect(mapStateToProps, mapDispatchToProps)(Chat);
