import './index.css'
import API_URL from '../../routes'

export default function MyApp({ Component, pageProps }) {
    return <Component {...pageProps} />
  }

  MyApp.getServerSideProps = async ctx => {
    const res = await fetch(`${API_URL}/bouquets`)
    const data = await res.json()
    return { items: data  }
}
