import API_URL from '../../../routes';

const Card = ({name, price, description, gallery}) => {
    return (
        <div className='card'>
            <h1>{name} {price} {description}</h1>
            <div className='card-image' style={{backgroundImage:`url(${API_URL}${gallery})`}}>

            </div>
        </div>
    )
}


export default Card;
