import React, {useState} from 'react';


const Slider = () => {
    let sliderArr = [1,2,3,4,5]
const [x, setX] = useState(0)

    const goLeft=() => {
        setX(x+100)
    }

    const goRight=() => {
        setX(x -100)
    }


    return(
        <div className='slider'>
            {
                sliderArr.map((item,index)=>{
                    return(
                        <div key={index} className='slide' style={{transform: `translateX(${x})%`}}>
                            {item}
                        </div>
                    )
                })
            }
            <button id = 'goLeft' className='slider-button' onClick={goLeft}>left</button>
            <button id ='goRight' className='slider-button' onClick={goRight}>right</button>
        </div>
    )
}

export default Slider;
