import React, {Fragment} from 'react'
import AreaInput from '../AreaInput/area-input.component';
import {sendMessage} from '../../firebase/firebase.utils';
import {connect} from 'react-redux'; 
import {setMessages} from '../../src/redux/chat/chat.actions';
import firebase from 'firebase/app';
import 'firebase/firestore';
import {motion} from 'framer-motion';
import ChatButton from '../Chatbutton/chat-button.component'


class  Chat extends React.Component {
    constructor() {
        super()
        this.state = {
            text:'',
        }
    }

    unsubscribe = (channel) => {
        
            if (!firebase.apps.length) {
                firebase.initializeApp({
                    apiKey: "AIzaSyABugTRykVjU3cO3nkANbnArtyZ_TPix1Q",
                    authDomain: "raspberry-dacc1.firebaseapp.com",
                    databaseURL: "https://raspberry-dacc1.firebaseio.com",
                    projectId: "raspberry-dacc1",
                    storageBucket: "raspberry-dacc1.appspot.com",
                    messagingSenderId: "435336134829",
                    appId: "1:435336134829:web:36258d0b8bdb9bf9810624",
                    measurementId: "G-E4XKDDZNWV"
                })
            }
                 
            
        
            firebase.firestore().collection('Chat').doc(`${channel}`).collection(`messages`).where('text','>','' )
            .onSnapshot((docs) => {
                const messages=[] 
                docs.forEach(function(doc) {
                    messages.push(doc.data());
                });
                this.props.setMessages(messages)    
                       
            })
                        
        
    }

    componentDidMount() {
        const {channel} = this.props
        this.unsubscribe(channel)
    }


    

    handleSubmit = async event => {
        event.preventDefault();
        const userRef = await sendMessage(this.state.text, this.props.channel);
        console.log(userRef)
    }


    handleChange = event => {


            this.setState({text:event.target.value})

    }
    render () {
        const {messages,channel} = this.props
        return (
            <motion.div>
                <div style={{height:100, width: 200}}>
                {   messages ?
                    messages.map(message=> <p>{message.text}</p>)
                    :null
                }
                </div>
                <form onSubmit={this.handleSubmit}>


                    <AreaInput 
                    name='text'
                    handleChange={this.handleChange}
                    value={this.state.text}
                    label='Your Review'
                    required
                    />       
                    <div>
                        <button type='submit'>
                            Send
                        </button>
                    </div>
                </form>
            </motion.div>
            
    )}
}

const mapStateToProps = state => ({
    messages: state.chat.messages,
    channel: state.chat.channel
})

const mapDispatchToProps = dispatch => ({
    setMessages: messages => dispatch(setMessages(messages))
})

export default connect(mapStateToProps, mapDispatchToProps)(Chat);
