import React, { Fragment } from 'react';
import { Provider, connect } from 'react-redux';
import App from 'next/app';
// import wrapper  from '../src/redux/store';
import { config } from '@fortawesome/fontawesome-svg-core';
import '@fortawesome/fontawesome-svg-core/styles.css';
import Layout from '../components/Layout/layout.component';

import './index.css';
import '../components/Slider/slider.styles.css';
import '../components/Header/header.styles.css';
import '../components/cart-icon/cart-icon.styles.css';
import makestore from '../src/redux/store';
import withRedux from 'next-redux-wrapper';
import { withReduxCookiePersist } from 'next-redux-cookie-wrapper';
import storage from 'redux-persist/lib/storage'



config.autoAddCss = false

class MyApp extends App {

  static async getInitialProps({Component, ctx}) {
      const pageProps = Component.getInitialProps ? await Component.getInitialProps(ctx) : {};

      //Anything returned here can be accessed by the client
      return {pageProps: pageProps};
  }

  render() {
      //pageProps that were returned  from 'getInitialProps' are stored in the props i.e. pageprops
      const {Component, pageProps, store} = this.props;

      return (              
                  <Provider store={store}>
                    <Layout>
                      <Component {...pageProps}/>
                    </Layout>
                  </Provider>
      );
  }
}



export default withReduxCookiePersist(makestore,{persistConfig : {
      key:'root',
      storage,
      whitelist: ['sort']
  }})(MyApp);

// export default withRedux(makestore)(MyApp)
