import API_URL from '../../../routes';
import CustomButton from '../CustomButton/CustomButton.component';
import { ReactComponent as Icon} from '../../static/commerce-and-shopping.svg'

const Card = ({name, price, description, gallery}) => {
    return (
        <div className='card'>            
            <div>
                <img className='card-image' src={`${API_URL}${gallery[0].url}`}/>
            </div>
            <div className='card-content'>
                <div>{name}</div>
                <div> {price} </div>
            </div>
            <div>
                <CustomButton>
                    <Icon className='icon'/>Add to Cart
                </CustomButton>
            </div>
        </div>
    )
}


export default Card;
