import React from 'react';
import FormInput from '../FormInput/formInput.component';

class  SignIn extends React.Component{
    constructor(props) {
        super(props)
        
        this.state = {
            email:'',
            password: '',
        }
    }

    handleChange = event => {
        const {value,name} =event.target;
        this.setState({[name]:value})
    }

    handleSubmit = event => {
        event.preventDefault();
    }
    
    render() {
        return (
            <div className='sign-in'>
                <div className='form-title'>
                    <h2>I already have an account</h2>
                    <span>Sign in with your email and password</span>
                </div>
                <form onSubmit={this.handleSubmit}>
                    <FormInput 
                    name='email'
                    type='email'
                    handleChange={this.handleChange}
                    value={this.state.email}
                    label='Email'
                    required
                    />
                    <FormInput 
                    name='password'
                    type='password'
                    label='Password'
                    value={this.state.password}
                    handleChange={this.handleChange}
                    required                   
                    />
                    <div className='buttons'>
                        <button className='signin-button' type='submit'>
                            SIGN IN
                        </button>
                    </div>
                    <h2>Sign in with Social Profile</h2>
                    <div className='buttons-left'>
                        {/* <button className='button-google' type='button'>
                            Sign in with Google
                        </button>

                        <button className='button-facebook' type ='button'>
                            Sign in with Facebook
                        </button> */}
                        <img className='google' src='/google.png' alt='google-icon' onClick={this.handleChange}/>
                        <img className='facebook' src='/facebook.png' alt='facebook-icon' onClick={this.handleChange}/>
                    </div>

                </form>
            </div>
        )
    }
}

export default SignIn;
