import API_URL from '../../routes.js';
import CustomButton from '../CustomButton/CustomButton.component';
import Slider from '../Slider/Slider.component';
import Link from 'next/link'


const Card = ({id, name, price, description, gallery}) => {
    return (
        <div className='card'>            
            <div>
            <Link href='itemgallery/[item]' as={`/itemgallery/${id}`}>
                <Slider images={gallery}/>
            </Link>
            </div>
            <div className='card-content'>
                <div>{name}</div>
                <div> {price} </div>
            </div>
            <div>
                <CustomButton>
                   <img className='icon' src ='/icon.svg'/> <div>Add to Cart</div>
                </CustomButton>
            </div>
        </div>
    )
}


export default Card;
