import API_URL from '../../../routes';

const Card = ({name, price, description, gallery}) => {
    return (
        <div className='card'>
            
            <div className='card-image' style={{backgroundImage:`url(${API_URL}${gallery[0].url})`}}>
                <img src={`${API_URL}${gallery[0].url}`}/>
            </div>
            <div className='card-content'>
                <div>{name}</div>
                <div> {price} </div>
            </div>
        </div>
    )
}


export default Card;
