const INITIAL_STATE = {
    channel: '',
    messages: []
}

const chatReducer = (state=INITIAL_STATE, action) => {
    switch(action.type) {
        case 'SET_MESSAGES':
            return {
                ...state,
                messages: action.payload
            }
        case 'SET_CHANNEL':
            return {
                ...state,
                channel: action.payload
            }
        default:
            return state
    }
}

export default chatReducer;
