import React, {Fragment, useState} from 'react';
import Chat from '../Chat/chat.component';
import ChatButton from '../Chatbutton/chat-button.component';
import { toggleCartHidden } from '../../src/redux/cart/cart.actions';


const ChatBody = () => {
    const [chatHidden,setChatHidden]= useState(true);
    const [newMessages, setNewMessages] = useState(false)

    const toggleChat = () => {
        setChatHidden(!chatHidden)
        setNewMessages(false)
    }

    return (
        <Fragment>
            { !chatHidden ?<Chat toggle={toggleChat} chatHidden={chatHidden} setNewMessages={setNewMessages}/> : null}
            <ChatButton onClick={toggleChat} newMessages={newMessages}/>
        </Fragment>

    )


}


export default ChatBody;
