import React from 'react';
import Card from '../Card/card.component';
import API_URL from '../../../routes';
import Pagination from 'react-js-pagination';
import AsyncSelect from 'react-select/async';



class Gallery extends React.Component {


    constructor(props){
        super(props)
        this.state = {
            //main state
            items:[],
            categories:[],
            //filters
            currentFilter:'',
            sorted: 'Newest',
            //pagination
            activePage:1,
            perPage:5,
            pageRange:3

        }
    }




    componentDidMount() {
        fetch(`${API_URL}/bouquets`)
        .then(res => res.json())
        .then(data => this.setState({items:[...this.state.items,...data]}))
        fetch(`${API_URL}/accessories`)
        .then(res => res.json())
        .then(data => this.setState({items:[...this.state.items,...data]}))
        fetch(`${API_URL}/categories`)
        .then(res => res.json())
        .then(data => this.setState({categories:data}))
    }

    onSelectChange = (event) => {
        if (event.target.value==='All'){
            this.setState({currentFilter:''});
        } else{
        this.setState({currentFilter:event.target.value});
        }
    }

    filterOptions =  
        this.state.categories.map(category => {
            return category.name
        })
    

    onSortChange = (event) => {
        this.setState({sorted:event.target.value});
    }

    handlePageChange(pageNumber) {
        this.setState({activePage: pageNumber});
    }

    render() {
        let filteredItems = this.state.items.filter(item => {
            return item.categories[0].name.includes(this.state.currentFilter)
        })
        
        if (this.state.sorted==='Lower Price') {
            (filteredItems.sort((a,b) => a.price - b.price))
        }else if (this.state.sorted==='Higher Price'){
            (filteredItems.sort((a,b) => b.price - a.price))
        }else if(this.state.sorted==='Newest') {
            filteredItems.sort((a,b) => {
                return new Date(b.date) -new Date(a.date)
            })
        }

        



        return(
            <div className='gallery-container'>
                <div className='filters'>

                    {console.log(this.filterOptions)}

                    {   
                        (this.state.categories[0]) ? 
                            // <select className='select-sort' onChange={this.onSelectChange} defaultValue='Category'>
                            //     <option disabled hidden>Category</option>
                            //     <option>All</option>
                            //     {this.state.categories.map(category => {
                            //         return <option key={category.id}>{category.name}</option>
                            //     })}
                            // </select>
                            <AsyncSelect loadOptions={this.filterOptions} onChange={this.onSelectChange}/>
                        : (
                            <option>Loading</option>
                        )
                    }
                    <select className='select-sort' onChange={this.onSortChange} defaultValue='Sort'>
                        <option disabled hidden>Sort</option>
                        <option>Lower Price</option>
                        <option>Higher Price</option>
                        <option>Newest</option>
                    </select>        
                </div>
                {
                    !this.state.items[0] ?
                        <h1>Loading</h1>
                    :(
                        <div className='gallery'>
                            
                            {
                            filteredItems.slice((this.state.activePage-1)*this.state.perPage,this.state.activePage*this.state.perPage).map(({id, name, ...otherProps }) =>{
                                    return <Card key={name} name={name} {...otherProps}/>
                            })}

                            {console.log((this.state.activePage-1)*this.state.perPage,this.state.activePage*this.state.perPage)}
                        </div>
                    )
                    
                }
                <div>
                    {
                        (filteredItems.length>this.state.perPage) ?
                            <Pagination 
                                activePage={this.state.activePage}
                                itemsCountPerPage={this.state.perPage}
                                totalItemsCount={filteredItems.length}
                                // pageRangeDisplayed={(filteredItems.length%this.state.perPage===0) ?
                                //     Math.floor(filteredItems.length/this.state.perPage) 
                                //     :Math.floor(filteredItems.length/this.state.perPage)+1 }
                                pageRangeDisplayed={this.pageRange}
                                onChange={this.handlePageChange.bind(this)}
                            />
                        : ('')
                    }
                </div>
            </div>
        )
    }
}


export default Gallery;
