import React, { useEffect, useRef } from "react";

const ClickOutside = ({ children, onClick }) => {
  const refs = React.Children.map(children, () => useRef());
  const handleClick = e => {
    const isOutside = refs.every(ref => {
        console.log(ref.current, e.target)
        if (e.target===<div class="cart-item-remove-button">âœ•</div>)
        {return False}
      return !ref.current.contains(e.target);

    });
    
    if (isOutside) {
      onClick();
    }
  };

  useEffect(() => {
    document.addEventListener("click", handleClick);

    return () => {
      document.removeEventListener("click", handleClick);
    };
  });

  return React.Children.map(children, (element, idx) =>
    React.cloneElement(element, { ref: refs[idx] })
  );
}

export default ClickOutside;
