export const addItemToCart = (cartItems, cartItemToAdd) => {
    const existingCartItem =cartItems.find(
        cartItem => cartItem.id ===cartItemToAdd.id
        );

    if (existingCartItem) {
        return cartItems.map(cartItem =>
            cartItem.id===cartItemToAdd.id
            ? {...cartItem, quantity: (cartItem.quantity +1)  }
            : cartItem 
            )
    }

    return [...cartItems, {...cartItemToAdd,quantity:1}]

}


export const addMultipleItemsToCart = (cartItems, cartItemToAdd) => {
    const existingCartItem =cartItems.find(
        cartItem => cartItem.id ===cartItemToAdd[0].id
        );

    if (existingCartItem) {
        return cartItems.map(cartItem =>
            cartItem.id===cartItemToAdd[0].id
            ? {...cartItem, quantity: (cartItem.quantity +cartItemToAdd[1])  }
            : cartItem 
            )
    }

    return [...cartItems, {...cartItemToAdd,quantity:cartItemToAdd[1]}]

}


export const removeItemFromCart = (cartItems, cartItemToRemove) => {
    const existingCartItem =cartItems.find(
        cartItem => cartItem.id ===cartItemToRemove.id
        );
    if (existingCartItem.quantity===1) {
        return cartItems
    }

    return cartItems.map(
        cartItem => 
        cartItem.id === cartItemToRemove.id 
        ? {...cartItem, quantity: (cartItem.quantity - 1)}
        : cartItem
    )
}
