const INITIAL_STATE = {
    cartItems:[],
    cartHidden: true
}

const cartReducer = (state = INITIAL_STATE, action) => {
    switch(action.type) {
        case 'TOGGLE_CART_HIDDEN':
            return {
                ...state,
                cartHidden: !cartHidden
            }
        default:
            return state
    }
}

export default cartReducer;
