import React, {useState} from 'react';
import Image from '../Image/image.component';
import {FontAwesomeIcon} from '@fortawesome/react-fontawesome';
import {faChevronLeft, faChevronRight} from '@fortawesome/free-solid-svg-icons';


const Slider = () => {
    let sliderArr = [
    <Image src='/temp/1.jpg'/>,
    <Image src='/temp/2.jpg'/>,
    <Image src='/temp/3.jpg'/>,
    <Image src='/temp/4.jpg'/>,
    <Image src='/temp/5.jpg'/>
]

    const [x, setX] = useState(0)

    const goLeft=() => {
        (x===0) ? setX(-100*(sliderArr.length-1)): setX(x + 100);
    }

    const goRight=() => {
        (x===-100*(sliderArr.length-1)) ? setX(0): setX(x - 100);
    }


    return(
        <div className='slider'>
            {
                sliderArr.map((item,index)=>{
                    return(
                        <div key={index} className='slide' style={{transform: `translateX(${x}%)`}}>
                            {item}
                        </div>
                    )
                })
            }
            {console.log(x)}
            <button id = 'goLeft' className='slider-button' onClick={goLeft}><FontAwesomeIcon icon={faChevronLeft}/></button>
            <button id ='goRight' className='slider-button' onClick={goRight}><FontAwesomeIcon icon = {faChevronRight}/></button>
        </div>
    )
}

export default Slider;
